/*popup in home page*/
:root{
    --top-space-10: 10px;
    --top-space-20: 20px;
    --bottom-space-10: 10px;
    --bottom-space-20: 10px;

}
.popup-container{
 
    width:500px;
    height:85px;
    position:fixed;
    left:100%;
    margin-right:30px;
    background-color: white;
    bottom:15%;
    border:1px solid red;
    border-radius:17px;
    .pop{
        img{
            width: auto;
            height: 80px;
            padding: 18px;
        }
        position:relative;
        display:flex;
        .pop-blurb{
            position:relative;
            display:flex;
            flex-direction: column;
            justify-content: center;
            align-items: flex-start;
            p{
                font-weight: bolder;
                font-size: 16px;
                font-family: sans-serif;
                margin-bottom: 5px;
            }
            i{
                font-style: italic;
                font-family:sans-serif !important;
            }
        }
    }
    .close{
        position:absolute;
        color:black;
        top:5px;
        right:15px;
    }

    @media(max-width:440px){
        transform:scale(0.78) !important;

    }
}



